import './Counter.css'
import PropTypes from 'prop-types';
import { useState } from "react"

export default function Counter() {
    const [count, setCount] = useState(0);
    return (
        <>
            <div className="count">{count}</div>
            <CounterButtonButton />
            <CounterButtonButton by={2} />
            <CounterButtonButton by={5} />
        </>
    )
}
function CounterButton({ by }) {
    function countIncrement() {
        setCount(count + by);
    }
    function countDecrement() {
        setCount(count - by);
    }
    return (
        <>
            <div>
                <div className="count">{count}</div>
                <button className="buttonStyle" onClick={countIncrement}>+{by}</button>
                <button className="buttonStyle" onClick={countDecrement}>-{by}</button>
            </div>
        </>
    )
}
Counter.propTypes = {
    by: PropTypes.number
}
Counter.defaultProps = {
    by: 1
}
