import { useState } from "react"

export default function ToDoApp() {
    return (
        <div>
            <div className="TodoApp">Todo Management Application</div>
            <LogInComponent />
            <WelcomeComponent />
        </div>
    )
}
function LogInComponent() {
    const [username, setUserName] = useState("Thinkitive")

    function handleUsernameChnage(){
        setUserName.current.value = "";
    }

    return (
        <div className="Login">
            <div className="LoginForm">
                <div>
                    <label htmlFor="">UserName : </label>
                    <input type="text" name="username" placeholder="Enter Your UserName" value={username}/>
                </div>
                <div>
                    <label htmlFor="">Password : </label>
                    <input type="password" name="password" placeholder="Enter Your Password"/>
                </div>
                <div>
                    <button type="button" name="login">LogIn</button>
                </div>
            </div>
        </div>
    )
}

function WelcomeComponent() {
    return (
        <div className="Welcome">
            {/* Welcome Component */}
        </div>
    )
}
