import './Counter.css'
import PropTypes from 'prop-types';
import { useState } from "react"

export default function CounterButton({by}) {
    const [count, setCount] = useState(0);
    function countIncrement() {
        setCount(count + by);
    }
    function countDecrement(){
        setCount(count - by);
    }
    return (
        <>
            <div>
                <div className="count">{count}</div>
                <button className="buttonStyle" onClick={countIncrement}>+{by}</button>
                <button className="buttonStyle" onClick={countDecrement}>-{by}</button>
            </div>
        </>
    )
}
Counter.propTypes ={
    by: PropTypes.number
}
Counter.defaultProps = {
    by :1
}
