import './Counter.css'
import { useState } from "react"

export default function Counter({by}) {
    const [count, setCount] = useState(0);
    function countIncrement() {
        setCount(count + by);
    }
    function countDecrement(){
        setCount(count - 1);
    }
    return (
        <>
            <div>
                <div className="count">{count}</div>
                <button className="buttonStyle" onClick={countIncrement}>+1</button>
                <button className="buttonStyle" onClick={countDecrement}>-1</button>
            </div>
        </>
    )
}
