import { useState } from "react"
import { BrowserRouter, Routes, Route, useNavigate, useParams, Link } from "react-router-dom"
export default function ToDoApp() {
    return (
        <div className="ToDoApp">
            <HeaderComponent />
            <BrowserRouter>
                <Routes>
                    <Route path="/" element={<LogInComponent />} />
                    <Route path="/login" element={<LogInComponent />} />
                    <Route path="/welcome/:username" element={<WelcomeComponent />} />
                    <Route path="/todo" element={<ListToDosComponent />} />
                    <Route path="*" element={<ErrorComponet />} />
                </Routes>
            </BrowserRouter>
            <FooterComponent />
        </div>
    )
}
function LogInComponent() {
    const [username, setUserName] = useState("Thinkitive")
    const [password, setPassword] = useState('')
    const [showSuccessMessage, setshowSuccessMessage] = useState(false)
    const [showErrorMessage, setshowErrorMessage] = useState(false)
    const navigate = useNavigate();

    function handleUsernameChnage(event) {
        setUserName(event.target.value)
    }

    function handlePasswordChnage(event) {
        setPassword(event.target.value)
    }

    function handleSubmit() {
        if (username === 'Thinkitive' && password === 'dummy') {
            console.log("Success")
            setshowSuccessMessage(true)
            setshowErrorMessage(false)
            navigate(`/welcome/${username}`)
        } else {
            console.log("Authentication Failed")
            setshowSuccessMessage(false)
            setshowErrorMessage(true)
        }
    }

    return (
        <div className="Login">
            <h1>Time To login</h1>
            {showSuccessMessage && <div className="successMessage">Authenticated Successfully.</div>}
            {showErrorMessage && <div className="errorMessage">Authentication Failed. Please check your credentials.</div>}
            <div className="LoginForm">
                <div>
                    <label htmlFor="">UserName : </label>
                    <input type="text" name="username" placeholder="Enter Your UserName" value={username} onChange={handleUsernameChnage} />
                </div>
                <div>
                    <label htmlFor="">Password : </label>
                    <input type="password" name="password" placeholder="Enter Your Password" value={password} onChange={handlePasswordChnage} />
                </div>
                <div>
                    <button type="button" name="login" onClick={handleSubmit}>LogIn</button>
                </div>
            </div>
        </div>
    )
}

function WelcomeComponent() {
    const { username } = useParams();
    console.log(username)
    return (
        <div className="WelcomeComponent">
            <h1>Welcome {username}</h1>
            <div>
                Manage Your Todos - <Link to="/todo" >Go Here</Link>
            </div>
        </div>
    )
}

function ErrorComponet() {
    return (
        <div className="ErrorComponet">
            <h1>We are working really hard!</h1>
            <div>
                Apologies for the 404. Reach out team at ABC-DEF-GHIJ.
            </div>
        </div>
    )
}

function ListToDosComponent() {
    const today = new Date()
    const targetDate = new Date(today.getFullYear() + 12, today.getMonth(), today.getDay())
    const todos = [
        { "id": "101", "description": "Learn React", done: false, targetDate: targetDate },
        { "id": "202", "description": "Learn Next", done: false, targetDate: targetDate },
        { "id": "303", "description": "Learn SpringBoot", done: false, targetDate: targetDate }
    ]

    return (
        <div className="ListToDosComponent">
            <h1>Things You Want To Do!</h1>
            <div>
                <table border={1}>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Description</th>
                            <th>Is Done?</th>
                            <th>Target Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            todos.map(
                                todo => <tr key={todo.id}>
                                    <td>{todo.id}</td>
                                    <td>{todo.description}</td>
                                    <td>{todo.done.toString()}</td>
                                    <td>{todo.targetDate.toDateString()}</td>
                                </tr>
                            )
                        }
                    </tbody>
                </table>
            </div>
        </div>
    )
}
function HeaderComponent() {
    return (
        <div className="header">
            header <hr />
        </div>
    )
}
function FooterComponent() {
    return (
        <div className="footer">
            <hr /> footer
        </div>
    )
}
function LogOutComponent(){
    return(
        <div className="LogOutComponent">
            <h1>You Are logged out</h1>
        </div>
    )
}
