import './Counter.css'
import PropTypes from 'prop-types';
import { useState } from "react"

export default function Counter(by) {
    function parentIncrement(){
        setCount(count + by)
    }
    const [count, setCount] = useState(0);
    return (
        <>
            <div className="countStyle">{count}</div>
            <CounterButton incrementMethod={parentIncrement}/>
            <CounterButton by={2} incrementMethod={parentIncrement}/>
            <CounterButton by={5} incrementMethod={parentIncrement}/>
        </>
    )
}
function CounterButton({ by , incrementMethod}) {
    const [count, setCount] = useState(0);
    function countIncrement() {
        setCount(count + by);
        incrementMethod({by})
    }
    function countDecrement() {
        setCount(count - by);
        incrementMethod()
    }
    return (
        <>
            <div>
                <div className="count">{count}</div>
                <button className="buttonStyle" onClick={countIncrement}>+{by}</button>
                <button className="buttonStyle" onClick={countDecrement}>-{by}</button>
            </div>
        </>
    )
}
CounterButton.propTypes = {
    by: PropTypes.number
}
CounterButton.defaultProps = {
    by: 1
}
